    Jt, psirz, Ip, fcurrt = EGGO.predict_model_from_boundary(Rb_target, Zb_target, pp_target, ffp_target, ecurrt_target, NNmodel, green, basis_functions, basis_functions_1d)

    dd = IMAS.dd()
    eqt = resize!(dd.equilibrium.time_slice)
    EGGO.fill_eqt(eqt, psirz, green, wall, pp_target, ffp_target, Btcenter, Rcenter, pend)
    IMAS.flux_surfaces(eqt, wall[:rlim], wall[:zlim])

    @test Jt !== nothing
    @test psirz !== nothing
    @test Ip !== nothing
    @test fcurrt !== nothing
end


